import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { IProdutos } from '../interfaces/produtos';

@Injectable({
  providedIn: 'root'
})
export class ProdutosService {

    api = 'http://http://localhost:8081/api/produtos';
  constructor(private http: HttpClient) { }

  buscarTodos() {
    return this.http.get<IProdutos[]>(this.api);
  }

  cadastrarUsuarios(produto: Partial<IProdutos>){
      return this.http.post(this.api, produto);
  }
}
