<h1 class="mt-3 mb-3">Usuários</h1>

<table class="table table-bordered table-striped">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Nome</th>
      <th scope="col">Idade</th>
      <th scope="col">Ativo</th>
      <th scope="col">Ações</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let usuario of usuarios">
      <th scope="row">{{ usuario.id }}</th>
      <td>{{ usuario.nome }}</td>
      <td>{{ usuario.idade }}</td>
      <td class="text-center">
        <i *ngIf="usuario.ativo else elseBlock" class="bi bi-hand-thumbs-up-fill text-success"></i>
        <ng-template #elseBlock>
          <i *ngIf="!usuario.ativo" class="bi bi-hand-thumbs-down-fill text-danger"></i>
        </ng-template>
      </td>
      <td class="text-center">
        <button type="button" class="btn btn-primary btn-sm" (click)="remover(usuario.id)">Deletar</button>
      </td>
    </tr>
  </tbody>
</table>
<a class="btn btn-primary" routerLink="/usuarios/cadastrar">Cadastrar</a>
