import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ProdutosService } from 'src/app/service/produtos.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-cadastrar-produtos',
  templateUrl: './cadastrar-produtos.component.html',
  styleUrls: ['./cadastrar-produtos.component.css'],
})

export class CadastrarProdutosComponent implements OnInit {


  produtoForm: Object|any;

  constructor(private produtosService: ProdutosService) {}

  ngOnInit(): void {
    this.produtoForm = new FormGroup({
      nome: new FormControl('', Validators.required),
      id: new FormControl('', Validators.required),
      preco :new FormControl('', Validators.required),
      codigoBarras :new FormControl('', Validators.required)
    });
  }



  enviar() {

    console.log('asd');return;

    if (this.produtoForm.valid) {
      const codigoBarrasControl = this.produtoForm.get('codigoBarras');
      const precoControl = this.produtoForm.get('preco');

      if (codigoBarrasControl && precoControl) {
        const produto: Partial<IProdutos> = {
          codigoBarras: codigoBarrasControl.value ? +codigoBarrasControl.value : undefined,
          preco: precoControl.value ? +precoControl.value : undefined
        };

        console.log(produto);

        this.produtosService.cadastrarProdutos(produto).subscribe(
          (result) => {
            Swal.fire('Confirmado', 'Produto cadastrado com sucesso', 'success');
            console.log(result);
          },
          (error) => {
            const { message } = error;
            console.error(error);

            Swal.fire('ALGO ESTÁ ERRADO', message, 'error');
          }
        );
      }
    } else {
      Swal.fire('Formulário Inválido', 'Preencha todos os campos corretamente.', 'error');
    }
  }


}
<h1 class="mt-3 mb-3">Cadastrar Produtos</h1>
<form [formGroup]="produtoForm" (submit)="enviar()">
  <div class="container">
    <div class="row">
      <div class="col">
        <div class="mb-3">
          <label for="nome" class="form-label">Nome do Produto</label>
          <input type="text" class="form-control" id="nome" formControlName="nome">
          <label for="idade" class="form-label">codigoBarras</label>
          <input type="number" class="form-control" id="codigoBarras" formControlName="codigoBarras">
          <label for="idade" class="form-label">Preço</label>
          <input type="number" class="form-control" id="preco" formControlName="preco">
        </div>
      </div>
      <div class="col">
      </div>
    </div>
    <a class="btn btn-secondary me-1" routerLink="/produtos">Voltar</a>
    <button class="btn btn-primary" type="submit" [disabled]="!produtoForm.invalid">Confirmar</button>
  </div>
</form>
