import { Component, Input } from '@angular/core';

@Component({
  selector: 'treinamento-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  @Input() nome: string = 'Digite seu nome';

  ngOnInit() {
    if (this.nome === 'Rafael') {
      console.log('to com fome');
    }
  }

  cadastroValido(): boolean {
    return this.nome === 'Ativado';
  }

}
<nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" routerLink="/">Sistema</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link" routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/usuarios" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">Usuarios</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/usuarios/cadastrar" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">Cadastro de usuários</a>
        </li>
      </ul>
    </div>
  </div>
</nav>
