import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ProdutosService } from 'src/app/service/produtos.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-cadastrar-produtos',
  templateUrl: './cadastrar-produtos.component.html',
  styleUrls: ['./cadastrar-produtos.component.css'],
})

export class CadastrarProdutosComponent {

  constructor(private produtosService: ProdutosService) {}

  produtoForm = new FormGroup({
    nome: new FormControl('', Validators.required),
    id: new FormControl('', Validators.required) // ID é obrigatório
  });

  enviar() {
    if (this.produtoForm.valid) {
      const produto: Partial<IProdutos> = {
        codigoBarras: this.produtoForm.get('codigoBarras').value,
        preco: this.produtoForm.get('preco').value,
      };

      console.log(produto);

      this.produtosService.cadastrarProdutos(produto).subscribe(
        (result) => {
          Swal.fire('Confirmado', 'Produto cadastrado com sucesso', 'success');
          console.log(result);
        },
        (error) => {
          const { message } = error;
          console.error(error);

          Swal.fire('ALGO ESTÁ ERRADO', message, 'error');
        }
      );
    } else {
      // Formulário inválido, trate conforme necessário (por exemplo, exiba uma mensagem de erro).
      Swal.fire('Formulário Inválido', 'Preencha todos os campos corretamente.', 'error');
    }
  }

}
<h1 class="mt-3 mb-3">Cadastrar Usuários</h1>
<form [formGroup]="produtoForm" >
  <div class="container">
    <div class="row">
      <div class="col">
        <div class="mb-3">
          <label for="nome" class="form-label">Nome</label>
          <input type="text" class="form-control" id="nome" formControlName="nome">
        </div>
      </div>
      <div class="col">
        <div class="mb-3">
          <label for="idade" class="form-label">Idade</label>
          <input type="number" class="form-control" id="idade" formControlName="idade">
        </div>
      </div>
    </div>
    <a class="btn btn-secondary me-1" routerLink="/usuarios">Voltar</a>
    <button class="btn btn-primary" type="submit" [disabled]="produtoForm.invalid">Cadastrar</button>
  </div>
</form>
