import { Component, OnInit } from '@angular/core';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ProdutosService } from 'src/app/service/produtos.service';


@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
export class ProdutosComponent implements OnInit{
    produtos!: IProdutos[];

  constructor
  (public listProdutos: ProdutosService) {}

  ngOnInit(): void{
    this.getProdutos();
  }

  getProdutos() {
    const requestGet = this.listProdutos.buscarTodos();

    requestGet.subscribe({
      next: (response) => {
        this.produtos = response;
        console.log(this.produtos);
      },
      error: (error) => {
        console.log(error);
      }
    });
  }


  // remover(id: number) {
  //   this.produtos = this.produtos.filter((produtos) => produtos.id !== id);
  // }
}
<h1 class="mt-3 mb-3">Produtos</h1>
<table class="table table-bordered table-striped">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Nome</th>
      <th scope="col">Codigo de Barras</th>
      <th scope="col">Preco</th>
    </tr>
      <tr *ngFor="let produto of produtos">
        <th scope="row">{{produto.id}}</th>
        <td>{{produto.codigoBarras}}</td>
        <td>{{produto.nome}}</td>
        <td>{{produto.preco}}</td>
        <td>
          <a class="btn-actions" routerLink="/cadastro">Edita </a>|
          <a class="btn-actions" routerLink="/cadastro">Excluir</a>
      </td>
    </tr>
  </thead>
  <tbody>
  </tbody>
</table>
<a class="btn btn-primary" routerLink="/produtos/cadastrar">Cadastrar</a>
